import React, {useState} from "react";
import { connect } from "react-redux";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { createSubTodo } from "../Redux/actions/subTodoAction";
import {toast} from 'react-toastify'

function FormCreateSubTodo(props){
    const {dispatchCreateTodoSubAction, history,id} = props;
    const {
        buttonLabel,
        className
    } = props;

    const [modal, setModal] = useState(false);
    const [activityTitle,setActivityTitle] = useState("")
    const [priority, setPriority] = useState("penting")
    const [note, setNote] = useState("")

    const toggle = () => setModal(!modal);

    const handleSubmit = (e) => {
        e.preventDefault();
        setModal(false);
        // console.log('e',activityTitle,priority,note)
        const data = {activityTitle,priority,note,id};
        dispatchCreateTodoSubAction(data, () => {
            console.log('data',data)
            toast.success('judul listmu sudah dibuat');
            history.replace('/detail/:todoCategoryID');     
        }, (message) => toast.error(`Error: ${message}`));
    }

    return (
        <>
        <Button color="danger" onClick={toggle}>
            Create
        </Button>
        <Modal isOpen={modal} toggle={toggle} className={className}>
            <ModalHeader toggle={toggle}>Create Sub Todo</ModalHeader>
            <Form onSubmit={handleSubmit}>
            <ModalBody>
                    <FormGroup>
                        <Label for="activityTitle">Title</Label>
                        <Input
                        type="text"
                        name="title"
                        id="activityTitle"
                        placeholder="with a placeholder"
                        onChange={(e) => setActivityTitle(e.target.value)}
                        />
                    </FormGroup>

                    <FormGroup>
                        <Label for="note">Note</Label>
                        <Input
                        type="text"
                        name="note"
                        id="note"
                        placeholder="with a placeholder"
                        onChange={(e) => setNote(e.target.value)}

                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="priority">Priority</Label>
                            <Input type="select" name="priority" id="priority" onChange={(e) => setPriority(e.target.value)} >
                            <option value="penting">Penting</option>
                            <option value="gapenting">Ga Penting</option>
                        </Input>
                    </FormGroup>
            </ModalBody>
            <ModalFooter>
                <Button type="submit" color="primary">
                    Save
                </Button>
                <Button color="secondary" onClick={toggle}>
                    Cancel
                </Button>
            </ModalFooter>
            </Form>

        </Modal>
        </>
    );
};

const mapStateToProps = state => ({

});

const mapDispatchToProps = (dispatch) => ({
    dispatchCreateTodoSubAction: (data, onSuccess, onError) =>
      dispatch(createSubTodo(data, onSuccess, onError)),
      
  });

export default connect(mapStateToProps, mapDispatchToProps)(FormCreateSubTodo);
