import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {getCategoryId, updateCategoryId } from "../Redux/actions/todolistAction";
import {getCategoryById} from "../Redux/actions/subTodoAction";
import {toast} from 'react-toastify'


function Sublist(props){
  const {dispatchGetTodoIdAction} = props;
  const [categoryTitle, setCategoryTitle] = useState("");

  console.log('props',props)

  // useEffect(()=> {
  //   const id = props.match.params.todoCategoryID;
  //   if(id){
  //     dispatchGetTodoIdAction(id, ({categoryTitle}) =>
  //     {
  //       setCategoryTitle(categoryTitle);
  //     });
  //   }
  // }, [dispatchGetTodoIdAction])


  return (
    <React.Fragment>
    <div className="container-sm">
         <div className="row">
        <div className="col">
          <h3>Add Todo</h3>
        </div>
      </div>

      <div className="row align-items-center ">
        <div className="col-md-6" >
          <form noValidate >
            <div className="form-group">
              <label htmlFor="categoryTitle">Title</label>
              <input
                noValidate
                id="categoryTitle"
                placeholder="judul"
                type="text"
                name="categoryTitle"
                value={categoryTitle}
                onChange={(e) => setCategoryTitle(e.target.value)}
                className="form-control"
              />
              
            </div>
          </form>
        </div>
      </div>
    </div>     
    </React.Fragment>
  );
};

const mapStateToProps = state => ({
  loading : state.loading,
  todoCategory : state.todoCategory
});

const mapDispatchToProps = (dispatch) => ({
    
  dispatchGetTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryId(id, onSuccess)),

  dispatchGetSubTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryById(id, onSuccess)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Sublist);
