import React, { useState } from "react";
import { connect } from "react-redux";
import { CreateTodo } from "../Redux/actions/todolistAction";

const ListPage = ({ dispatchCreateTodoAction }) => {
  const [title, setTitle] = useState("");

  return (
    <React.Fragment>
    <div className="container-sm">
         <div className="row">
        <div className="col">
          <h3>Add Todo</h3>
        </div>
      </div>

      <div className="row align-items-center ">
        <div className="col-md-6" >
          <form noValidate>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                noValidate
                id="title"
                placeholder="judul"
                type="text"
                name="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="form-control"
              />
            </div>
            <div className="mt-5">
                <button type="submit" className="btn btn-primary mr-2 btn-md">
                    save | <i className="fas fa-save"></i>
                </button>
            </div>
          </form>
        </div>
      </div>
    </div>
     
    </React.Fragment>
  );
};

const mapDispatchToProps = (dispatch) => ({
  dispatchCreateTodoAction: (data, onSuccess, onError) =>
    dispatch(CreateTodo(data, onSuccess, onError)),
});

export default connect(null, mapDispatchToProps)(ListPage);
