import React, {useEffect, useState} from 'react'
import { getCategoryById} from "../Redux/actions/subTodoAction";
import {getCategoryId} from '../Redux/actions/todolistAction'
import {connect} from 'react-redux'

const ShowSubList = ({dispatchGetTodoIdAction, match}) => {

    const [categoryTitle, setCategoryTitle] = useState("");

    useEffect(()=> {
        const {id} = match.params;
        if(id){
          dispatchGetTodoIdAction(id, ({categoryTitle}) =>
          {
            setCategoryTitle(categoryTitle);
          });
        }
      }, [dispatchGetTodoIdAction, match.params])

    return (
        <div >
            <input
                
                 id="categoryTitle"
                 type="text"
                 name="categoryTitle"
                 value={categoryTitle}
                 onChange={(e) => setCategoryTitle(e.target.value)}
            /> 
        </div>
    )
}


const mapDispatchToProps = (dispatch) => ({
    dispatchGetTodoIdAction: (todoCategoryID, onSuccess) => 
        dispatch(getCategoryId(todoCategoryID, onSuccess)),

    dispatchGetSubTodoAction: (id, onSuccess) => 
        dispatch(getCategoryById(id, onSuccess))
}) 


export default connect(mapDispatchToProps)(ShowSubList)
