import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {getCategoryId} from "../Redux/actions/todolistAction";
import {getCategoryById, createSubTodo, getDeleteSubCategory, getDetailSubCategory } from "../Redux/actions/subTodoAction";
import FormCreateSubTodo from './formCreateSubTodo';
import { toast } from "react-toastify";
import FormEditSubTodo from "./../pages/editSubTodo";



function Sublist(props){
  const {dispatchGetTodoIdAction, dispatchGetSubTodoIdAction,
        dispatchCreateTodoSubAction, dispatchDeleteTodoSubAction, 
        dispatchGetSubDetailTodoIdAction} = props;

  const [title, setTitle] = useState("");
  const [data, setData] = useState([]);
  const [todoCategoryID, setTodoCategoryID] = useState(null);
  const [selectSubTodo, setSelectSubTodo] = useState("")
  const [detaild, setDetailId] = useState("")

  useEffect(()=> {
    const id = props.match.params.todoCategoryID;
    setTodoCategoryID(id);
    if(id){
      dispatchGetTodoIdAction(id, ({categoryTitle}) =>
      {
        setTitle(categoryTitle);
      });
      dispatchGetSubTodoIdAction(id, data => {
        setData(data);
      });

    }
  }, [dispatchGetTodoIdAction])



  const showModal = (event, id) => {
    event.preventDefault();
    setSelectSubTodo(id);
    window.$("#confirmationModal").modal("show");
  };

  const handleDelete = () => {
    const id = props.match.params.todoCategoryID;
    dispatchDeleteTodoSubAction(
      selectSubTodo,
      () => {
        window.$("#confirmationModal").modal("hide");
        toast.success("Udah kehapus Nih!");
        dispatchGetSubTodoIdAction(id, data => {
          setData(data);
        })
      },
      (message) => {
        window.$("#confirmationModal").modal("hide");
        toast.error(`Error:${message}`);
      }
    );
  };

  return (
    <React.Fragment>
    <div className="container-sm">
         <div className="row">
        <div className="col">
          <h3>Add Todo</h3>
        </div>
      </div>

      <div className="row align-items-center ">
        <div className="col-md-6" >
          <h1>{title}</h1>
        </div>
        {data.map(datas =>
        <div style={{marginRight:"10px"}} className="card">
          <p>judul : {datas.activityTitle}</p>
          <p>prioritas : {datas.priority}</p>
          <p>notes : {datas.note}</p>
          <button className="btn btn-secondary" href="/" onClick={(e) => showModal(e, datas.todoID)}>
              delete
          </button>
          <FormEditSubTodo/>
        </div>
        )}
      </div>
    </div>     
    <div>
    <FormCreateSubTodo todoCategoryID = {todoCategoryID} dispatchCreateTodoSubAction={dispatchCreateTodoSubAction}/>
    
    <Modal handleDelete={handleDelete}/>
    </div>
    
    </React.Fragment>
  );
};

const mapStateToProps = state => ({
  loading : state.loading,
  todoCategory : state.todoCategory,
  subTodoCategory : state.subTodoList,
});

const mapDispatchToProps = (dispatch) => ({
    
  dispatchGetTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryId(id, onSuccess)),

  dispatchGetSubTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryById(id, onSuccess)),

  dispatchGetSubDetailTodoIdAction: (id, onSuccess) => 
    dispatch(getDetailSubCategory (id, onSuccess)),

  dispatchCreateTodoSubAction: (data, onSuccess, onError) =>
    dispatch(createSubTodo(data, onSuccess, onError)),   
  
  dispatchDeleteTodoSubAction: (id, onSuccess, onError) =>
    dispatch(getDeleteSubCategory (id, onSuccess, onError)),
  
  
});

export default connect(mapStateToProps, mapDispatchToProps)(Sublist);


const Modal = ({ handleDelete }) => (
  <div className="modal" id="confirmationModal">
    <div role="document" className="modal-dialog">
      <div className="modal-content">
        <div className="modal-header">
          <h5 className="modal-title"> confirmation </h5>
        </div>
        <div className="modal-body">
          <p>yakin mau hapus ?</p>
        </div>
        <div className="modal-footer">
          <button
            type="button"
            data-dismiss="modal"
            className="btn btn-secondary"
          >
            Enggak
          </button>
          <button
            className="btn btn-secondary"
            onClick={handleDelete}
            data-dismiss="modal"
          >
            Iya dong
          </button>
        </div>
      </div>
    </div>
  </div>
);
