import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {getCategoryId } from "../Redux/actions/todolistAction";
import {getCategoryById} from "../Redux/actions/subTodoAction"


const EditTodo = ({ dispatchGetTodoIdAction,match }) => {
  
  const [categoryTitle, setCategoryTitle] = useState("");


  useEffect(()=> {
    const {id} = match.params;
    if(id){
      dispatchGetTodoIdAction(id, ({categoryTitle}) =>
      {
        setCategoryTitle(categoryTitle);
      });
    }
  }, [dispatchGetTodoIdAction, match.params])



  return (
    <React.Fragment>
    <div className="container-sm">
         <div className="row">
        <div className="col">
          <h3>Add Todo</h3>
        </div>
      </div>

      <div className="row align-items-center ">
        <div className="col-md-6" >
          <form noValidate >
            <div className="form-group">
              <label htmlFor="categoryTitle">Title</label>
              <input
                noValidate
                id="categoryTitle"
                placeholder="judul"
                type="text"
                name="categoryTitle"
                value={categoryTitle}
                onChange={(e) => setCategoryTitle(e.target.value)}
                className="form-control"
              />
             
            </div>
          </form>
        </div>
      </div>
    </div>
     
    </React.Fragment>
  );
};

const mapDispatchToProps = (dispatch) => ({
  dispatchGetSubTodoAction: (todoCategoryID, onSuccess) => 
  dispatch(getCategoryById(todoCategoryID, onSuccess)),
    
  dispatchGetTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryId(id, onSuccess))
});

export default connect(null, mapDispatchToProps)(EditTodo);
