import React, {useState} from "react";
import { connect } from "react-redux";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input} from 'reactstrap';
import {toast} from 'react-toastify'
import { updateSubCategory, getDetailSubCategory } from "../Redux/actions/subTodoAction";


function FormEditSubTodo(props){
    const {dispatchGetSubDetailTodoIdAction,
        todoCategoryID,dispatchUpdateSubTodoAction, todoID} = props;
    const {
        className
    } = props;

    const [modal, setModal] = useState(false);
    const [activityTitle,setActivityTitle] = useState("")
    const [priority, setPriority] = useState("penting")
    const [note, setNote] = useState("")

    const toggle = () => setModal(!modal);

    // const handleSubmit = (e) => {
    //     e.preventDefault();
    //     const SubId = props.todoID;
    //     const data = {activityTitle,priority,note,todoCategoryID};
    //     console.log('data',data)
    //     dispatchUpdateSubTodoAction(data, SubId, () => {
    //         // toast.success('judul listmu sudah dibuat');
    //         // history.replace('/detail/:todoCategoryID');     
    //     }, (message) => toast.error(`Error: ${message}`));
    // }

    // const handleEdit = () => {
    //     const SubId = props.todoID;
    //     const data = {activityTitle, priority,note, todoCategoryID, todoID};
    //     if(SubId){
    //         dispatchGetSubDetailTodoIdAction(SubId, SubData =>{
    //             console.log('test',SubData)
    //             setActivityTitle(SubData.activityTitle)
    //             setPriority(SubData.priority)
    //             setNote(SubData.note)
    //             setModal(true);
    //         });
    //         dispatchUpdateSubTodoAction(SubId, data, () => {
    //             console.log('data', data)
    //             toast.success('sudah diupdate');
    //             // history.replace('/detail/:todoCategoryID')
    //         }, (message) => toast.error(`Error: ${message}`) )
    //     }
    // }
    
    const handleSubmit = event => {
        const subId = props.todoID;
        event.preventDefault();
        const data = {activityTitle, priority,note, todoCategoryID};
        dispatchGetSubDetailTodoIdAction(subId, data, () => {
            toast.success('sudah diupdate');
            // history.replace('/detail/:todoCategoryID')
        }, (message) => toast.error(`Error: ${message}`) )
    }

    return (
        <>
        <Button color="primary" onClick={handleSubmit}>
            Edit
        </Button>
        <Modal isOpen={modal} toggle={toggle} className={className}>
            <ModalHeader toggle={toggle}>Create Sub Todo</ModalHeader>
            <Form >
            <ModalBody>
                <FormGroup>
                    <Label for="activityTitle">Title</Label>
                    <Input
                    type="text"
                    name="title"
                    id="activityTitle"
                    placeholder="with a placeholder"
                    onChange={(e) => setActivityTitle(e.target.value)}
                    value={activityTitle}
                    className="form-control"
                    />
                </FormGroup>

                <FormGroup>
                    <Label for="note">Note</Label>
                    <Input
                    type="text"
                    name="note"
                    id="note"
                    placeholder="with a placeholder"
                    onChange={(e) => setNote(e.target.value)}
                    value={note}
                    className="form-control"
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="priority">Priority</Label>
                        <Input type="select" value={priority} name="priority" id="priority" onChange={(e) => setPriority(e.target.value)} >
                        <option value="penting">Penting</option>
                        <option value="gapenting">Ga Penting</option>
                    </Input>
                </FormGroup>
            </ModalBody>
            <ModalFooter>
                <Button type="submit" color="primary" onClick={toggle}>
                    Save
                </Button>
                <Button color="secondary" onClick={toggle}>
                    Cancel
                </Button>
            </ModalFooter>
            </Form>
        </Modal>
        </>
    );
};

const mapStateToProps = state => ({
    subTodoCategory : state.subTodoList,
});

const mapDispatchToProps = (dispatch) => ({
    dispatchGetSubDetailTodoIdAction: (id, onSuccess) => 
        dispatch(getDetailSubCategory (id, onSuccess)),

    dispatchUpdateSubTodoAction: (id, data, onSuccess, onError) => 
        dispatch(updateSubCategory(id, data, onSuccess, onError)),
});

export default connect(mapStateToProps, mapDispatchToProps)(FormEditSubTodo);
