import React, {useState, useEffect} from "react";
import { connect } from "react-redux";
import { Button, Form, FormGroup, Label, Input} from 'reactstrap';
import {toast} from 'react-toastify'
import { updateSubCategory, getCategoryById} from "../Redux/actions/subTodoAction";




const EditTodo = ({ dispatchGetSubTodoIdAction, match, dispatchUpdateSubTodoAction,history }) => {
  
    const [activityTitle, setActivityTitle] = useState("");
    const [note, setNote] = useState("");
    const [priority, setPriority] = useState("");
  
    useEffect(()=> {
      console.log(match);
      const {id} = match.params;
      if(id){
        (id, ({activityTitle, note, priority }) =>
        {
          setActivityTitle(activityTitle);
          setNote(note);
          setPriority(priority);
        });
      }
    }, [dispatchGetSubTodoIdAction, match.params])
  
  
    const handleOnSubmit = event => {
      const {id} = match.params;
      event.preventDefault();
      const data = {activityTitle, note, priority};
      console.log('bisa nih')
      dispatchUpdateSubTodoAction(id, data, () => {
           toast.success('sudah diupdate');
      }, (message) => toast.error(`Error : ${message}`));
  }
  
  
    return (
      <React.Fragment>
          <Form onSubmit={handleOnSubmit}>
               <FormGroup>
                        <Label for="activityTitle">Title</Label>
                        <Input
                        type="text"
                        name="title"
                        id="activityTitle"
                        placeholder="with a placeholder"
                        onChange={(e) => setActivityTitle(e.target.value)}
                        />
                    </FormGroup>

                    <FormGroup>
                        <Label for="note">Note</Label>
                        <Input
                        type="text"
                        name="note"
                        id="note"
                        placeholder="with a placeholder"
                        onChange={(e) => setNote(e.target.value)}

                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="priority">Priority</Label>
                            <Input type="select" name="priority" id="priority" onChange={(e) => setPriority(e.target.value)} >
                            <option value="penting">Penting</option>
                            <option value="gapenting">Ga Penting</option>
                        </Input>
                    </FormGroup>
                    <Button className="btn btn-primary" type="submit"> save </Button>
          </Form>
           
      </React.Fragment>
    );
  };
  
  const mapDispatchToProps = (dispatch) => ({
    dispatchUpdateSubTodoAction: (id, data, onSuccess, onError) => 
      dispatch(updateSubCategory(id, data, onSuccess, onError)),
      
    dispatchGetSubTodoIdAction: (id, onSuccess) => 
        dispatch(getCategoryById(id, onSuccess)),
  });
  
  export default connect(null, mapDispatchToProps)(EditTodo);
