import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {getCategoryId} from "../Redux/actions/todolistAction";
import {getCategoryById,createSubTodo} from "../Redux/actions/subTodoAction";
import FormCreateSubTodo from './formCreateSubTodo';
import {toast} from 'react-toastify'

function Sublist(props){
  const {dispatchGetTodoIdAction,dispatchGetSubTodoIdAction,dispatchCreateTodoSubAction} = props;
  const [title, setTitle] = useState("");
  const [data, setData] = useState([]);
  const [id, setId] = useState(null);

  useEffect(()=> {
    const id = props.match.params.todoCategoryID;
    if(id){
      dispatchGetTodoIdAction(id, ({categoryTitle}) =>
      {
        setTitle(categoryTitle);
        setId(id);
      });
      dispatchGetSubTodoIdAction(id, data => {
        setData(data);
      })
    }
  }, [dispatchGetTodoIdAction])

  return (
    <React.Fragment>
    <div className="container-sm">
         <div className="row">
        <div className="col">
          <h3>Add Todo</h3>
        </div>
      </div>

      <div className="row align-items-center ">
        <div className="col-md-6" >
          <h1>{title}</h1>
        </div>
        {data.map(datas =>
        <div style={{marginRight:"10px"}} className="card">
          <p>judul : {datas.activityTitle}</p>
          <p>notes : {datas.priority}</p>
          <p>notes : {datas.note}</p>
          <button className="btn btn-primary">
              
          </button>
        </div>
        )}
      </div>
    </div>     
    <div>
      <FormCreateSubTodo id={id} dispatchCreateTodoSubAction={dispatchCreateTodoSubAction}/>
    </div>
    </React.Fragment>
  );
};

const mapStateToProps = state => ({
  loading : state.loading,
  todoCategory : state.todoCategory,
  subTodoCategory : state.subTodoList,
});

const mapDispatchToProps = (dispatch) => ({
    
  dispatchGetTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryId(id, onSuccess)),

  dispatchGetSubTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryById(id, onSuccess)),

  dispatchCreateTodoSubAction: (data, onSuccess, onError) =>
    dispatch(createSubTodo(data, onSuccess, onError)),   
});

export default connect(mapStateToProps, mapDispatchToProps)(Sublist);
