import React, {useEffect, useState} from "react";
import { Button, Form, FormGroup, Label, Input,} from 'reactstrap';
import {toast} from 'react-toastify';
import {connect} from 'react-redux';
import {getCategoryById,createSubTodo} from '../Redux/actions/subTodoAction';


const FormCreateSubTodo = ({ dispatchCreateTodoSubAction, match, 
                             dispatchGetSubTodoIdAction}) => {
  
    const [modal, setModal] = useState(false);
    const [activityTitle,setActivityTitle] = useState("")
    const [priority, setPriority] = useState("penting")
    const [note, setNote] = useState("")

    const toggle = () => setModal(!modal);

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {activityTitle,priority,note};
        console.log('data',data)
        dispatchCreateTodoSubAction(data, () => {
            // toast.success('judul listmu sudah dibuat');
            // history.replace('/detail/:todoCategoryID');     
        }, (message) => toast.error(`Error: ${message}`));        
    }

    useEffect(() => {
        console.log(match);
        const {id} = match.params;
        const data = {activityTitle, priority, note}
        console.log('data', data)
        if(id) {
            dispatchGetSubTodoIdAction(id, data, () => {
                console.log('berhasil');
            })
        }
    })


    return (
        <>
     
            <Form onSubmit={handleSubmit}>
    
                    <FormGroup>
                        <Label for="activityTitle">Title</Label>
                        <Input
                            type="text"
                            name="title"
                            id="activityTitle"
                            placeholder="with a placeholder"
                            onChange={(e) => setActivityTitle(e.target.value)}
                        />
                    </FormGroup>

                    <FormGroup>
                        <Label for="note">Note</Label>
                        <Input
                            type="text"
                            name="note"
                            id="note"
                            placeholder="with a placeholder"
                            onChange={(e) => setNote(e.target.value)}

                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="priority">Priority</Label>
                            <Input type="select" name="priority" id="priority" onChange={(e) => setPriority(e.target.value)} >
                            <option value="penting">Penting</option>
                            <option value="gapenting">Ga Penting</option>
                        </Input>
                    </FormGroup>
          
                <Button type="submit" color="primary" onClick={toggle}>
                    Save
                </Button>
                <Button color="secondary" onClick={toggle}>
                    Cancel
                </Button>
         
            </Form>

     
        </>
    );
};

const mapDispatchToProps = (dispatch) => ({
    
    dispatchGetSubTodoIdAction: (id, onSuccess) => 
      dispatch(getCategoryById(id, onSuccess)),

    dispatchCreateTodoSubAction: (data, onSuccess, onError) =>
      dispatch(createSubTodo(data, onSuccess, onError)),   
    
  });
  


export default  connect(null, mapDispatchToProps)(FormCreateSubTodo);
