import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {getCategoryId} from "../Redux/actions/todolistAction";
import {getCategoryById} from "../Redux/actions/subTodoAction";
import {toast} from 'react-toastify'

function Sublist(props){
  const {dispatchGetTodoIdAction,dispatchGetSubTodoIdAction} = props;
  const [title, setTitle] = useState("");
  const [data, setData] = useState([]);

  useEffect(()=> {
    const id = props.match.params.todoCategoryID;
    if(id){
      dispatchGetTodoIdAction(id, ({categoryTitle}) =>
      {
        setTitle(categoryTitle);
      });
      dispatchGetSubTodoIdAction(id, data => {
        setData(data);
      })
    }
  }, [dispatchGetTodoIdAction])

  console.log('subTodoCategory',data)

  return (
    <React.Fragment>
    <div className="container-sm">
         <div className="row">
        <div className="col">
          <h3>Add Todo</h3>
        </div>
      </div>

      <div className="row align-items-center ">
        <div className="col-md-6" >
          <h1>{title}</h1>
        </div>
        {data.map(datas =>
        <div>
          <p>{datas.note}</p>
        </div>
        )}
      </div>
    </div>     
    </React.Fragment>
  );
};

const mapStateToProps = state => ({
  loading : state.loading,
  todoCategory : state.todoCategory,
  subTodoCategory : state.subTodoList,
});

const mapDispatchToProps = (dispatch) => ({
    
  dispatchGetTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryId(id, onSuccess)),

  dispatchGetSubTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryById(id, onSuccess)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Sublist);
