import React, {useState} from "react";
import { connect } from "react-redux";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


function FormCreateSubTodo(props){
    const {
        buttonLabel,
        className
      } = props;
    
      const [modal, setModal] = useState(false);
    
      const toggle = () => setModal(!modal);


  return (
    <>
      <div>
        <Button color="danger" onClick={toggle}>
          Create
        </Button>
        <Modal isOpen={modal} toggle={toggle} className={className}>
          <ModalHeader toggle={toggle}>Create Sub Todo</ModalHeader>
          <ModalBody>
            <Form>
              <FormGroup>
                <Label for="exampleEmail">Email</Label>
                <Input
                  type="text"
                  name="email"
                  id="exampleEmail"
                  placeholder="with a placeholder"
                />
              </FormGroup>

              <FormGroup>
                <Label for="activityTitle">Title</Label>
                <Input
                  type="text"
                  name="text"
                  id="activityTitle"
                  placeholder="with a placeholder"
                />
              </FormGroup>
              <FormGroup>
                <Label for="priority">Priority</Label>
                <Input type="select" name="select" id="priority">
                  <option>Penting</option>
                  <option>Ga Penting</option>
                </Input>
              </FormGroup>
            </Form>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={toggle}>
              Do Something
            </Button>{" "}
            <Button color="secondary" onClick={toggle}>
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
      </div>
    </>
  );
};

const mapStateToProps = state => ({

});

const mapDispatchToProps = (dispatch) => ({

});

export default connect(mapStateToProps, mapDispatchToProps)(FormCreateSubTodo);
