import React, {useState} from "react";
import { connect } from "react-redux";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


function FormCreateSubTodo(props){
    const {
        buttonLabel,
        className
    } = props;

    const [modal, setModal] = useState(false);
    const [activityTitle,setActivityTitle] = useState("")
    const [priority, setPriority] = useState("")
    const [note, setNote] = useState("")

    const toggle = () => setModal(!modal);

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('e',activityTitle,priority,note)
    }

    return (
        <>
        <Button color="danger" onClick={toggle}>
            Create
        </Button>
        <Modal isOpen={modal} toggle={toggle} className={className}>
            <ModalHeader toggle={toggle}>Create Sub Todo</ModalHeader>
            <Form onSubmit={handleSubmit}>
            <ModalBody>
                    <FormGroup>
                        <Label for="activityTitle">Title</Label>
                        <Input
                        type="text"
                        name="title"
                        id="activityTitle"
                        placeholder="with a placeholder"
                        onChange={(e) => setActivityTitle(e.target.value)}
                        />
                    </FormGroup>

                    <FormGroup>
                        <Label for="note">Note</Label>
                        <Input
                        type="text"
                        name="note"
                        id="note"
                        placeholder="with a placeholder"
                        onChange={(e) => setNote(e.target.value)}

                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="priority">Priority</Label>
                            <Input type="select" name="priority" id="priority" onChange={(e) => console.log('e')} >
                            <option value="penting">Penting</option>
                            <option value="gapenting">Ga Penting</option>
                        </Input>
                    </FormGroup>
            </ModalBody>
            <ModalFooter>
                <Button type="submit" color="primary">
                    Save
                </Button>
                <Button color="secondary" onClick={toggle}>
                    Cancel
                </Button>
            </ModalFooter>
            </Form>

        </Modal>
        </>
    );
};

const mapStateToProps = state => ({

});

const mapDispatchToProps = (dispatch) => ({

});

export default connect(mapStateToProps, mapDispatchToProps)(FormCreateSubTodo);
