import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {getCategoryId, updateCategoryId } from "../Redux/actions/todolistAction";
import {toast} from 'react-toastify'


const Sublist = ({ dispatchGetTodoIdAction, match, dispatchUpdateTodoAction,history }) => {
  
  const [categoryTitle, setCategoryTitle] = useState("");


  useEffect(()=> {
    const {id} = match.params.todoCategoryID;
    console.log('id',match.params.todoCategoryID)
    if(id){
      dispatchGetTodoIdAction(match.params.todoCategoryID, ({categoryTitle}) =>
      {
        setCategoryTitle(categoryTitle);
      });
    }
  }, [dispatchGetTodoIdAction, match.params])




  return (
    <React.Fragment>
    <div className="container-sm">
         <div className="row">
        <div className="col">
          <h3>Add Todo</h3>
        </div>
      </div>

      <div className="row align-items-center ">
        <div className="col-md-6" >
          <form noValidate >
            <div className="form-group">
              <label htmlFor="categoryTitle">Title</label>
              <input
                noValidate
                id="categoryTitle"
                placeholder="judul"
                type="text"
                name="categoryTitle"
                value={categoryTitle}
                onChange={(e) => setCategoryTitle(e.target.value)}
                className="form-control"
              />
              
            </div>
          </form>
        </div>
      </div>
    </div>
     
    </React.Fragment>
  );
};

const mapStateToProps = state => ({
  loading : state.loading,
  todoCategory : state.todoCategory
});

const mapDispatchToProps = (dispatch) => ({
  dispatchUpdateTodoAction: (id, data, onSuccess, onError) => 
    dispatch(updateCategoryId(id, data, onSuccess, onError)),
    
  dispatchGetTodoIdAction: (id, onSuccess) => 
    dispatch(getCategoryId(id, onSuccess))
});

export default connect(mapStateToProps, mapDispatchToProps)(Sublist);
