import React, {useState} from 'react';
import {connect} from 'react-redux'
import {registerUser} from '../Redux/actions/authAction'


const RegisterForm = ({ dispatchRegisterAction }) => {
    const [fullName, setFullName] = useState('')
    const [username, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    
    const handleSubmit = (event) => {
      event.preventDefault();
      dispatchRegisterAction(fullName, username, password, email, 
        () => console.log('berhasil membuat akun'), 
        (message) => console.log(`Error : ${message}`));
    };

    return (
      <div>
        <React.Fragment>
          <form noValidate onSubmit={handleSubmit}>
          <div className="form-group mb-3">
            <label for="fullname" class="form-label">
              Full Name
            </label>
            <input
              type="text"
              class="form-control"
              name="fullName"
              id="fullname"
              placeholder="urname"
              value={fullName}
              onChange = {(e) => setFullName(e.target.value)}
            />
          </div>
          <div className="form-group mb-3">
            <label for="username" class="form-label">
              User Name
            </label>
            <input
              type="text"
              class="form-control"
              name="username"
              id="username"
              placeholder="user name"
              value={username}
              onChange = {(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-group mb-3">
            <label for="email" class="form-label">
              Email address
            </label>
            <input
              type="email"
              class="form-control"
              name="email"
              id="email"
              placeholder="name@example.com"
              value={email}
              onChange = {(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group mb-3">
            <label for="password" class="form-label">
              Password
            </label>
            <input
              type="password"
              class="form-control"
              name="password"
              id="password"
              placeholder="your password"
              value={password}
              onChange = {(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">
            Register
          </button>
          </form>
         
        </React.Fragment>
      </div>
    );
}

mapDispatchToProps = dispatch => ({
  dispatchRegisterAction : (username, fullName, email, password, onSuccess, onError) => {
    dispatch(registerUser({fullName, email, username, password}, onSuccess, onSuccess))
  }
})

export default connect(null, mapDispatchToProps)(RegisterForm);
