import * as constants from '../constant'



export const registerUser = (data, onSuccess, onError) => ({
    type: constants.API,
    payload: {
        method : 'POST', 
        url : '/api/ApplicationUser/Register', 
        data, 
        success: (response) => (setUserInfo(respond)), 
        postProcessSuccess: onSuccess,
        postProcessError : onError
    }

});


const setUserInfo = (data) => {
    const parsedToken = JSON.parse(atob(data.token.split('.')[1]));
    const userInfo = {
        userId: parsedToken.id, 
        greeting: `${parsedToken.username} ${parsedToken.fullname}`,
        token: data.token, 
        isLoggedIn : true 
    };
    localStorage.setItem('USER_INFO', JSON.stringify(userInfo));
    return {type: constants.USER_INFO, payload: userInfo};
};
